using System;
using System.Globalization;
using System.Text.RegularExpressions;

public class DateTimeConverter
{
    public static void Main()
    {
        string inputFormat = "MMM dd \\u00b7 hh:mm a";
        CultureInfo culture = CultureInfo.InvariantCulture;

        string inputDateTime = "December 21 \u00b7 1:00 - 1:50pm";
        DateTime pacificDateTime = DateTime.MinValue;
        try
        {
            Regex timeRegex = new Regex(@"(\d+)\:([\d0-9]{2})(?:(? :am|pm)|(? :[AP]M))");
            Match match = timeRegex.Match(inputDateTime);
            string hour = match.Groups[1].Value;
            string minute = match.Groups[2].Value;
            string ampm = match.Groups[3].Value;

            string[] dateParts = inputDateTime.Split(new[] {'' }, StringSplitOptions.RemoveEmptyEntries);
            string[] dateValues = dateParts[0].Split(new[] {'' }, StringSplitOptions.RemoveEmptyEntries);

            int day = int.Parse(dateValues[1]);
            string monthName = dateValues[0];
            int year = 2021;

            DateTime inputTime = DateTime.ParseExact($"{monthName} {day}, {hour} : {minute} {ampm}", "MMMM dd, hh : mm tt", culture);
            inputTime = DateTime.SpecifyKind(inputTime, DateTimeKind.Utc);

            TimeSpan timeSpan = inputTime.TimeOfDay;
            int offsetMinutes = -8 * 60; // Los Angeles time zone offset from UTC in minutes
            DateTime convertedTimeUtc = new DateTime(inputTime.Year, inputTime.Month, inputTime.Day, timeSpan.Hours, timeSpan.Minutes, timeSpan.Seconds, inputTime.Kind);
            DateTime pacificTime = convertedTimeUtc.AddMinutes(offsetMinutes);
            pacificDateTime = pacificTime;
        }
        catch (FormatException ex)
        {
            Console.WriteLine($"Error parsing date: {ex.Message}");
            return;
        }

        DateTime taipeiDateTime = pacificDateTime.AddHours(16); // 16 hours difference between Pacific Time and Taipei Time

        string taipeiTime = taipeiDateTime.ToString("yyyy/MM/dd HH:mm", CultureInfo.InvariantCulture);

        Console.WriteLine(taipeiTime + " Taipei Time"); // "2021/12/21 21:00 Taipei Time"
    }
}
