std::string convertMarkdownToHTML(const std::string& markdown) {
    // Define HTML formatted string
    std::string html;

    // Apply regular expressions to match and replace Markdown patterns with HTML equivalents

    // Bold
    std::regex pattern_md_bold("(.*)\\*([^*]*?)\\*(.*)" );
    std::regex pattern_html_bold("<\\1><b>\\2</b></\\1>";

    // Italic
    std::regex pattern_md_italic("(.*)_([^_]*?)_(.*)" );
    std::regex pattern_html_italic("<\\1><i>\\2</i></\\1>");

    // Header
    std::regex pattern_md_header("^(#{1,6})(.+)\\1$(.*)" );
    std::regex pattern_html_header("<h\\1>\\2</h\\1>\\3");

    // Link
    std::regex pattern_md_link("([\\w+\\-\\.]+)\\s*(\\()([^)]*?)\\/((?:\"[^\"]*?\"|[^\s]*)?)\\s*(\\))" );
    std::regex pattern_html_link("<a href=\"\\4\">\\2</a>\\6");

