import java.util.Scanner;

public class MickPaidLessons {
    public static void main(String[] args) {
        Scanner input = new Scanner(System.in);

        System.out.print("Enter the hourly rate of the lessons: ");
        double hourlyRate = input.nextDouble();

        System.out.print("Enter the number of lessons: ");
        int numLessons = input.nextInt();

        System.out.print("Enter the duration of each lesson (in hours): ");
        double hoursPerLesson = input.nextDouble();

        double totalLessonCost = hourlyRate * numLessons * hoursPerLesson;
        double receivedPrice = 800 - totalLessonCost;

        double lessonPerCost = totalLessonCost / (numLessons * hoursPerLesson);

        System.out.printf("Mick received $%.2f\n", receivedPrice);
        System.out.printf("Mick paid a total of $%.2f for %d lessons, %.2f hours each.\n", totalLessonCost, numLessons, hoursPerLesson);
        System.out.printf("Mick paid $%.2f per lesson.\n", lessonPerCost);
    }
}

// Example usage:
// Enter the hourly rate of the lessons: 10
// Enter the number of lessons: 40
// Enter the duration of each lesson (in hours): 2
// Output: Mick received $0.00
//         Mick paid a total of $32,000.00 for 40 lessons, 2.00 hours each.
//         Mick paid $1600.00 per lesson.﻿#javascript
function calculateReceivedPrice(lessonRate, lessonCount, hoursPerLesson) {
  const lessonTotalCost = lessonRate * lessonCount * hoursPerLesson;
  const receivedPrice = 800 - lessonTotalCost;

  const lessonPerCost = lessonTotalCost / (lessonCount * hoursPerLesson);

  return [receivedPrice.toFixed(2), lessonTotalCost.toFixed(2), lessonPerCost.toFixed(2)];
}

// Example usage:
const lessonRate = 800 / (40 * 2); // $10 per hour
const lessonCount = 40;
const hoursPerLesson = 2;

const [receivedPrice, lessonTotalCost, lessonPerCost] = calculateReceivedPrice(lessonRate, lessonCount, hoursPerLesson);
console.log(`Mick received ${receivedPrice}`);
console.log(`Mick paid a total of ${lessonTotalCost} for ${lessonCount} lessons, ${hoursPerLesson} hours each.`);
console.log(`Mick paid ${lessonPerCost} per lesson.`);

// Output: Mick received "0.00"
//         Mick paid a total of "32000.00" for 40 lessons, 2.00 hours each.
//         Mick paid "1600.00" per lesson.

