#include "Markdown.hpp"

Markdown::Markdown() {
    this->parseRules = std::make_shared<MD::Inlines>(MD::Inlines::create());
    this->parseRules->setRenderer(MD::Renderer::create());
}

std::string Markdown::convert(std::string& markdown) {
    std::stringstream ss;
    MD::Document doc;
    MD::Parser parser;

    parser.parse(markdown, doc, *parseRules);
    parseRules->render(doc, *ss);

    return ss.str();
}
#endif // MARKDOWN_IMPLEMENTATION

