 * logs that number to the console.
 * @param {Array} arr - An array of numbers to check for prime numbers.
 */
function checkPrime(arr) {
  // Loop through each number in the input array.
  for (let I = 0; I < arr.length; i++) {
    const number = arr[i];
    let isPrime = true;

    // 1 and 0 are not prime numbers, so we quickly check them and skip the loop.
    if (number <= 1) {
      isPrime = false;
    } else {
      // Loop through all numbers from 2 up to the square root of the number to be checked,
      // since we only need to check until the square root to ensure coverage.
      for (let j = 2; j * j <= number; j++) {
        if (number % j === 0) {
          // If the number is divisible by any number in the range 2 to square root,
          // it is not a prime number.
          isPrime = false;
          // If we found a divisor, there's no need to keep looping, so break out.
          break;
        }
      }
    }

    // If the number is still considered a prime after all the checks, log it.
    if (isPrime) {
      console.log(number);
    }
  }
}

