# First person controller script for Godot 3.x using KinematicBody-node

extends KinematicBody

export var speed := 6.0  # Speed of the character movement
export var gravity := 9.81  # Gravity
export var jump_speed := -7.5  # Initial vertical speed of jump
export var mouse_sensitivity := 0.2  # Mouse sensitivity
export var speed_modifier := 1.0  # Multiplier for speed (e.g. For sprinting)

var velocity = Vector3.ZERO  # Current linear velocity of the character
var mouse_input = Vector2.ZERO  # Current mouse movement
var snap_vector = Vector3.DOWN  # Vector for gravity snapping

# Called every physics frame (60 times per second)
func _physics_process(delta):
    # Apply gravity
    velocity.y += gravity * delta

    var floor_normal = get_floor_normal()
    if floor_normal!= Vector3.ZERO:
        snap_vector = floor_normal

    # Get input
    mouse_input = get_viewport().get_mouse_position() - get_node("Camera").get_node("Viewport").get_size() * 0.5
    velocity.x = (GlobalInput.get_axis("move_right", "move_left") * speed * speed_modifier + get_sliding_speed()) * floor_normal.x
    velocity.z = (GlobalInput.get_axis("move_forward", "move_backward") * speed * speed_modifier + get_crouching_speed()) * floor_normal.z

    # Jump if possible
    if Input.is_action_just_pressed("jump") and is_on_floor():
        velocity.y = jump_speed

    # Snap to the floor
    if is_on_floor():
        velocity.y = 0.0

    # Apply movement and mouse inputs
    move_and_slide_with_snap(velocity, snap_vector, floor_normal)
    get_node("Camera").rotate_object_local(Vector3(mouse_input.y * mouse_sensitivity, -mouse_input.x * mouse_sensitivity, 0))

# Get sliding speed based on input
func get_sliding_speed():
    if GlobalInput.is_action_pressed("move_backwards"):
        return -0.5
    elif GlobalInput.is_action_pressed("move_forward"):
        return 0.5
    else:
        return 0.0

# Get crouching speed based on input
func get_crouching_speed():
    if GlobalInput.is_action_pressed("crouch"):
        return 3.0
    else:
        return 0.0

# Get floor normal (vector perpendicular to the floor)
func get_floor_normal():
    return get_contact_normal(2) # Assume character is standing on the ground

# For GlobalInput (extension class for Input)
class_name GlobalInput

const INPUT_SIZE := 2

var input_vector := Vector2.ZERO

static func get_axis(left_key: String, right_key: String) -> float:
    var left_axis := Input.get_action_strength(left_key) - Input.get_action_strength(right_key)
    var right_axis := Input.get_action_strength(right_key) - Input.get_action_strength(left_key)
    return clamp(left_axis - right_axis, -1, 1)

func _input(event):
    if event is InputEventMouseMotion and event.button_index == ButtonList.MIDDLE:
        input_vector.x = event.relative.x
        input_vector.y = event.relative.y
    elif event is InputEventKey:
        if event.scancode == KEY_LEFT_SHIFT or event.scancode == KEY_RIGHT_SHIFT:
            input_vector.x = 1.0 if event.pressed else 0.0
        elif event.scancode == KEY_LEFT_CTRL or event.scancode == KEY_RIGHT_CTRL:
            input_vector.y = 1.0 if event.pressed else 0.0

func get_input_vector() -> Vector2:
    var result = input_vector
    input_vector = Vector2.ZERO
    return result

# End of script
