public class UniqueWords {
    private Map<String, String> cache;

    public UniqueWords() {
        cache = new HashMap<>();
    }

    public void addWord(String word) {
        if (cache.containsKey(word)) {
            // If the word already exists in the cache, use its reference
            // instead of creating a new one.
