﻿#Python code to find longest common subsequence of two input strings using dynamic programming

# Function to find longest common subsequence using dynamic programming
def lcs(str1, str2):
    # Create a 2D table to store results of subproblems
    table = [[0] * (len(str2) + 1) for _ in range(len(str1) + 1)]

    # Fill out the table
    for i in range(1, len(str1) + 1):
        for j in range(1, len(str2) + 1):
            if str1[i - 1] == str2[j - 1]:
                table[i][j] = table[i - 1][j - 1] + 1
            else:
                table[i][j] = max(table[i - 1][j], table[i][j - 1])

    # Extract longest common subsequence from table
    res = ""
    I, j = len(str1), len(str2)
    while I > 0 and j > 0:
        if str1[i - 1] == str2[j - 1]:
            res = str1[i - 1] + res
            I -= 1
            j -= 1
        elif table[i - 1][j] > table[i][j - 1]:
            I -= 1
        else:
            j -= 1

    return res

# Example usage
str1 = "ABCDGH"
str2 = "AEDFHR"
print(lcs(str1, str2)) # "ADH"

