using namespace std;

string convert_markdown_to_html(const string& markdown) {
  string html = "";

  // Split the markdown into lines
  vector<string> lines = split(markdown, '\n');

  for (const auto& line : lines) {
    // Check for headers
    if (starts_with(line, "#")) {
      // Convert the header level to an HTML heading tag
      int level = stoi(line.substr(1, 1));
      html += string("<h") + to_string(level) + ">" + line.substr(level) + "</h>\n";
      continue;
    }

    // Check for bold and italic text
    if (starts_with(line, "*") && line.size() > 1 && line.back() == '*') {
      string text = line.substr(1, line.size() - 2);
      html += string("<b>");
      html += text;
      html += string("</b>\n");
      continue;
    }

    if (starts_with(line, "_") && line.size() > 1 && line.back() == '_') {
      string text = line.substr(1, line.size() - 2);
      html += string("<i>");
      html += text;
      html += string("</i>\n");
      continue;
    }

    // Check for lists
    if (starts_with(line, "-") || starts_with(line, "*")) {
      // Convert the line to an HTML list item
      string text = line.substr(2);
      html += string("<li>");
      html += text;
      html += string("</li>\n");
      continue;
    }

    // Treat the line as regular text
    html += line + "\n";
  }

  return html;
}

vector<string> split(const string& s, char c) {
  vector<string> v;
  boost::split(v, s, boost::is_any_of(std::to_string(c)), boost::token_compress_on);
  return v;
}
