def rank_pro_consumer(companies):
    # Define a dictionary containing the scores for each company
    scores = {
        "Microsoft": 8,
        "Google": 9,
        "Nintendo": 7,
        "Sony": 6,
        "EA": 5
    }

    # Sort the dictionary by scores in descending order
    sorted_scores = {k: v for k, v in sorted(scores.items(), key=lambda item: item[1], reverse=True)}

    # Print the sorted list of companies by pro-consumer score
    print("Ranking Pro-Consumer Companies:")
    for index, company in enumerate(sorted_scores):
        print(f"{index+1}. {company}: {sorted_scores[company]}")

