﻿# Discord Bot using Discord.js v14
const { Intents, Client, MessageEmbed } = require("discord.js");

const client = new Client({
    intents: [
        Intents.FLAGS.GUILDS,
        Intents.FLAGS.GUILD_MESSAGES,
    ],
});

client.on("ready", () => {
    console.log("Bot is online");
});

client.on("interactionCreate", async (interaction) => {
    if (!interaction.isCommand()) return;

    const { commandName, options } = interaction;

    if (commandName === "ban") {
        const member = interaction.guild.members.cache.get(options.getUser("user").id);

        if (!member) {
            await interaction.reply({
                content: "Member not found",
                ephemeral: true,
            });
            return;
        }

        await interaction.member.permissions.has("BAN_MEMBERS")
           ? await member.ban()
            : await interaction.reply({
                content: "You don't have the necessary permissions",
                ephemeral: true,
            });

        await interaction.reply({
            content: `${member.user.tag} has been banned`,
            ephemeral: false,
        });
    }
});

// Add token for bot here
client.login("YOUR_DISCORD_BOT_TOKEN");



