using namespace std;

string convertToHTML(string& markdown) {
    string html = "";

    // Convert headers
    const regex headerRegex(R"(^#+[\s]+(.*)\s*$)", regex::extended);
    smatch headerMatch;
    while (regex_search(markdown.c_str(), headerMatch, headerRegex)) {
        string level = to_string(headerMatch.size() - 1);
        string content = headerMatch[1];
        html += "<h" + level + ">" + content + "</h" + level + ">\n";
        boost::algorithm::regex_replace(markdown, headerRegex, "", boost::regex::format_first_only);
    }

    // Convert unordered lists
    const regex listRegex(R"(^ *\*\+[\s]+(.*)\s*$)", regex::extended);
    smatch listMatch;
    while (regex_search(markdown.c_str(), listMatch, listRegex)) {
        string content = listMatch[1];
        html += "<ul>\n";
        boost::algorithm::regex_replace(markdown, listRegex, "", boost::regex::format_first_only);
        const regex itemRegex(R"(^ *\*\-[\s]+(.*)\s*$)", regex::extended);
        while (regex_search(markdown.c_str(), listMatch, itemRegex)) {
            string item = listMatch[1];
            html += "<li>" + item + "</li>\n";
            boost::algorithm::regex_replace(markdown, itemRegex, "", boost::regex::format_first_only);
        }
        html += "</ul>\n";
    }

    // Convert ordered lists
    const regex orderedListRegex(R"(^ *\d+\.[\s]+(.*)\s*$)", regex::extended);
    smatch orderedListMatch;
    while (regex_search(markdown.c_str(), orderedListMatch, orderedListRegex)) {
        string content = orderedListMatch[1];
        html += "<ol>\n";
        boost::algorithm::regex_replace(markdown, orderedListRegex, "", boost::regex::format_first_only);
        const regex itemRegex(R"(^ *\d+\.[\s]+(.*)\s*$)", regex::extended);
        while (regex_search(markdown.c_str(), orderedListMatch, itemRegex)) {
            string item = orderedListMatch[1];
            html += "<li>" + item + "</li>\n";
            boost::algorithm::regex_replace(markdown, itemRegex, "", boost::regex::format_first_only);
        }
        html += "</ol>\n";
    }

    // Convert code blocks
    const regex codeRegex(R"(^ ```[\s]+([^\'\"]*)[\s]+(.*)\s*$)", regex::extended);
    smatch codeMatch;
    while (regex_search(markdown.c_str(), codeMatch, codeRegex)) {
        string type = codeMatch[1];
        string content = codeMatch[2];
        html += "<pre><code class=\"" + type + "\">" + content + "</code></pre>\n";
        boost::algorithm::regex_replace(markdown, codeRegex, "", boost::regex::format_first_only);
    }

    // Convert images
    const regex imageRegex(R"(^\! \[\]\(([^'\"]*)\)\s*$)", regex::extended);
    smatch imageMatch;
    while (regex_search(markdown.c_str(), imageMatch, imageRegex)) {
        string src = imageMatch[1];
        html += "<img src=\"" + src + "\" alt=\"Image\">\n";
        boost::algorithm::regex_replace(markdown, imageRegex, "", boost::regex::format_first_only);
    }

    return html;
}

int main() {
