def longest_common_subsequence(str1, str2):
    # Initialize a 2D array to hold the LCS
    lcs_table = [[0 for j in range(len(str2) + 1)] for I in range(len(str1) + 1)]

    # Initialize the first row and column of the LCS table
    for I in range(1, len(str1) + 1):
        lcs_table[i][0] = 0

    for j in range(1, len(str2) + 1):
        lcs_table[0][j] = 0

    # Fill in the rest of the LCS table using dynamic programming
    for I in range(1, len(str1) + 1):
        for j in range(1, len(str2) + 1):
            if str1[i - 1] == str2[j - 1]:
                lcs_table[i][j] = lcs_table[i - 1][j - 1] + 1
            else:
                lcs_table[i][j] = max(lcs_table[i - 1][j], lcs_table[i][j - 1])

    # Extract the longest common subsequence from the LCS table
    lcs = ""
    I, j = len(str1), len(str2)
    while I > 0 and j > 0:
        if str1[i - 1] == str2[j - 1]:
            lcs = str1[i - 1] + lcs
            I -= 1
            j -= 1
        elif lcs_table[i - 1][j] > lcs_table[i][j - 1]:
            I -= 1
        else:
            j -= 1

    return lcs
# Example usage:
print(longest_common_subsequence("abcdgh", "abcdaf"))
# Output: "abcd"

