<?php

// Function to find common elements in two arrays
function findCommonElements(array $array1, array $array2) {

  $result = array();

  // Looping through the first array
  foreach ($array1 as $value) {

    // Checking the value's presence in the second array
    if (in_array($value, $array2)) {

      // If found, add it to the result array
      $result[] = $value;
    }
  }

  // Return the result array
  return $result;
}

// Example usage
$firstArray = array(1, 2, 3, 4, 5);
$secondArray = array(3, 4, 5, 6, 7);

$commonElements = findCommonElements($firstArray, $secondArray);

print_r($commonElements);

# Output: Array ( [0] => 3 [1] => 4 [2] => 5 )

?>

